
/**
 * Handles the language links if using the selectbox language menu
 *
 * @author Caspar Stuebs <caspar@gosign.de>
 *
 * @return returns init Function which can be called on document.ready
 */
var selectboxLanguageMenu = (function() {
	/**
	 * initializes the langauge menu
	 *
	 * @author Caspar Stuebs <caspar@gosign.de>
	 *
	 * @return void
	 */
	function init() {
		var selectBox = jQuery('select.tx-golanguage-piMenu-languageSelect');

		selectBox.children('option.actualLanguage').attr('selected', true);

		selectBox.change(function() {
			window.location.href = selectBox.children(':selected').val();
		});
	}

	return {
		init:init
	}
})();

jQuery(document).ready(function() {
	selectboxLanguageMenu.init();
});
